import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ThingsToDo = ({ location }) => {
  const [places, setPlaces] = useState([]);

  useEffect(() => {
    const fetchPlaces = async () => {
      const response = await axios.get(
        `https://maps.googleapis.com/maps/api/place/textsearch/json?query=things+to+do+in+${location}&key=YOUR_API_KEY`
      );
      setPlaces(response.data.results.slice(0, 5)); // Get top 5
    };

    fetchPlaces();
  }, [location]);

  if (!places.length) return <p>Loading places...</p>;

  return (
    <div>
      <h2>Top 5 Things to Do in {location}</h2>
      <ul>
        {places.map((place) => (
          <li key={place.place_id}>{place.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default ThingsToDo;
